import React, { useEffect } from 'react';
import axios from 'axios';

const OrdenarPlaylists = ({ setPlaylists }) => {
  const [isAnimating, setIsAnimating] = React.useState(false);
  const [order, setOrder] = React.useState('asc'); // Estado para manejar el orden

  const fetchSortedPlaylists = async () => {
    setIsAnimating(true);

    console.log('SE ESTÁ INTENTANDO ORDENAR');

    const token = localStorage.getItem('spotify_access_token');
    if (!token) {
      console.error('Token de acceso no disponible en localStorage');
      setIsAnimating(false);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:4000/playlists?order=${order}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      const sortedPlaylists = response.data;
      console.log('Playlists ordenadas:', sortedPlaylists);
      setPlaylists(sortedPlaylists);
    } catch (error) {
      console.error('Error al obtener playlists ordenadas:', error);
    } finally {
      setIsAnimating(false);
    }
  };

  useEffect(() => {
    console.log('Token de acceso almacenado en localStorage:', localStorage.getItem('spotify_access_token'));
  }, []);

  return (
    <div>
      <button onClick={() => { setOrder('asc'); fetchSortedPlaylists(); }}>Ordenar de menor a mayor duración</button>
      <button onClick={() => { setOrder('desc'); fetchSortedPlaylists(); }}>Ordenar de mayor a menor duración</button>
      {isAnimating && <p>Ordenando playlists...</p>}
    </div>
  );
};

export default OrdenarPlaylists;
